parameters.jsCode:
// Skills counter function for n8n workflow data
const skillCounts = {};

// Get the aggregated data from the first item
const aggregatedData = $input.first().json.data;

// Process all items in the aggregated data array
aggregatedData.forEach(item => {
  // Get skills and description_skills from each item
  const skills = item.skills || [];
  const descriptionSkills = item.description_skills || [];

  // Combine both arrays
  const allSkills = [...skills, ...descriptionSkills];

  // Count each skill (normalized to lowercase)
  allSkills.forEach(skill => {
    const normalizedSkill = skill.toLowerCase();
    skillCounts[normalizedSkill] = (skillCounts[normalizedSkill] || 0) + 1;
  });
});

// Convert to array and sort by count (descending)
const sortedSkills = Object.entries(skillCounts)
  .map(([skill, count]) => ({ skill, count }))
  .sort((a, b) => b.count - a.count);

// Return in required format
const result = {
  skills: sortedSkills
};

return [{ json: result }];

// N8N Code Node - Transform nested output structure to flat structure
// Input: Array with objects containing nested "output" property
// Output: Array with flattened objects containing only "description_summary"

const inputData = $input.all();

const transformedData = inputData.map(item => {
  // Extract the description_summary from the nested output object
  return {
    description_summary: item.json.output.description_summary
  };
});

return transformedData;

// Get the aggregated data array
const data = $input.all()[0].json.data;

// Create a map to store combined results
const resultMap = new Map();

// Process each item in the data array
data.forEach(item => {
  let key, count;

  // Determine if this is a skill item or name item
  if (item.skill) {
    key = item.skill;
    count = item.count;
  } else if (item.name) {
    key = item.name;
    count = item.count;
  } else {
    return; // Skip items that don't have skill or name
  }

  // If key already exists, add the counts together
  if (resultMap.has(key)) {
    resultMap.set(key, resultMap.get(key) + count);
  } else {
    resultMap.set(key, count);
  }
});

// Convert map to array of objects
const result = Array.from(resultMap.entries()).map(([name, count]) => ({
  name: name,
  count: count
}));

// Sort by count in descending order (optional)
result.sort((a, b) => b.count - a.count);

return result.map(item => ({ json: item }));
